mapper.select:
select t.* from( select a.appr_seq, a.title, a.drafterid, a.draftername, a.regdate ,p.approvalstatus, p.approvaltype, p.signerid, p.statusname ,row_number() over(order by a.appr_seq desc)as rnum from approval a, participant p, fldrmbr fm, folder f where a.appr_seq = p.appr_seq and a.appr_seq = fm.fldrmbrid and f.fldrid = fm.fldrid and p.approvalstatus = 4098 and a.status not in(4096,256) and p.approvaltype = 4 and p.flag = '0'
and fm.fldrid = #{fldrid} and f.applid = 2010
)t where rnum between #{rowStart} and #{rowEnd}

select t.* from( select a.appr_seq, a.title, a.drafterid, a.draftername, a.regdate, p.signerid, a.status ,row_number() over(order by a.appr_seq desc)as rnum from approval a, participant p, fldrmbr fm, folder f where a.appr_seq = p.appr_seq and a.appr_seq = fm.fldrmbrid and f.fldrid = fm.fldrid and p.flag = '0' and a.status != 256 and p.signerid = #{signerid} and (p.approvaltype =1024 and a.status =4096 or(p.approvaltype !=1024 and a.status != 4096))
and fm.fldrid = #{fldrid} and f.applid = 2020
group by a.appr_seq, a.title, a.drafterid, a.draftername, a.regdate, p.signerid , a.status )t where rnum between #{rowStart} and #{rowEnd}

	select t.*
	    from(
	        select a.appr_seq, a.title, a.drafterid, a.draftername, a.approvaldate,a.receivers,
	            row_number() over(order by appr_seq desc)as rnum
	           from approval a, fldrmbr fm, folder f
	        where a.status =256
	        and a.docattr = '1'
			and a.poststatus = '1'
	       	and a.drafterdeptid = #{drafterdeptid}
	       	and a.appr_seq = fm.fldrmbrid
            and f.fldrid = fm.fldrid
            and fm.fldrid =#{fldrid}
            and f.applid = 4030
            and f.ownerid =#{ownerid}

		)t	    
	where rnum between #{rowStart} and #{rowEnd}

select d.*
from(
	select a.*, row_number() over(order by docregno desc)as rnum
	from approval a, fldrmbr2 fm2
	where 1=1

	and a.status = 256
	and a.appr_seq = fm2.fldrmbrid
	and fm2.fldrid = #{fldrid}

)d
where rnum between #{rowStart} and #{rowEnd}

mapper.update:
update approval
	set docregno = #{docregno}, regno = #{regno}
where appr_seq = #{appr_seq}

update approval
	set status = 4096
where appr_seq = #{appr_seq}

update approval
	set status = 1, title=#{title}, content=#{content}, receivers = #{receivers}, sendername = #{sendername}, 
	poststatus = #{poststatus}, folderid =#{folderid}, foldername =#{foldername}, bizunitcd = #{bizunitcd}, docattr = #{docattr}
where appr_seq = #{appr_seq}
and drafterid = #{drafterid}

update approval
set attachcnt = #{attachcnt}
where appr_seq = #{appr_seq}

update approval
set poststatus = '2', senddate = to_char(sysdate,'YYYY/MM/DD HH24:MI:SS'), sendid = drafterdeptid
where appr_seq = #{appr_seq}

mapper.select:
select s.appr_seq
	from send s, approval a 
where s.receiverid = #{receiverid}
and s.receiptappr_seq = a.appr_seq
and a.appr_seq =#{appr_seq}

mapper.update:
update approval
	set  orgdeptfolderid = #{orgdeptfolderid}, folderid = #{folderid}
where appr_seq = #{appr_seq}

update approval
	set bizunitcd = #{bizunitcd}, orgdraftdeptid =#{orgdraftdeptid}
where folderid = #{folderid}
